// import styles from "../styles/Slider.module.scss";
import React from "react";
import useSlider from "./useSlider";

const slides = [
  {
    title: "Slide 1",
    url: "src/components/Carousel/imgs/malttonS1",
  },
  {
    title: "Slide 2",
    url: "src/components/Carousel/imgs/malttonS2",
  },
  {
    title: "Slide 3",
    url: "src/components/Carousel/imgs/malttonS3",
  },
];

function Slider() {
  const slideWidth = 300;

  return (
    <div
      className="container"
      style={{
        backgroundColor: "grey",
        width: slideWidth,
        height: slideWidth,
      }}
    >
      <div
        className="scroller"
        style={{
          // our counter offset will go here
          transform: `translate3d(-${offset * slideWidth}px,0,0)`,
          width: `${slides.length * slideWidth}px`,
        }}
      >
        {slides.map((slide, index) => (
          <div
            key={slide.title}
            className="slide"
            style={{
              backgroundColor: "grey",
              width: slideWidth,
              height: slideWidth,
            }}
          >
            <img
              className={styles.imgSlide}
              src={slide.url}
              alt={slide.title}
            />
          </div>
        ))}
      </div>
    </div>
  );
}

export default Slider;
