import styles from "../styles/Header.module.scss";
import Products from "/Products";
import Footer from "/Footer";
import { useState, React } from "react";
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";

function Header() {
  const [showMenu, setShowMenu] = useState(false);

  function handleClick() {
    setShowMenu(!showMenu);
  }

  function getNavClasses() {
    if (showMenu) {
      return styles.nav_open;
    }
    return "";
  }

  return (
    <Router>
      <div className={`${getNavClasses()} ${styles.header}`}>
        <button
          onClick={handleClick}
          className={styles.nav_toggle}
          aria-label="toggle navigation"
        >
          <span className={styles.hamburger}>
            <i className="ri-menu-line"></i>
          </span>
        </button>
        <h1 className={styles.title}>M√ÅLTTON</h1>
        <button
          className={styles.carrito}
          aria-label="ir al carrito"
          // onClick={}
        >
          <i className="ri-shopping-bag-fill"></i>
        </button>
        <nav className={styles.nav}>
          <ul className={styles.nav__list}>
            <li className={styles.nav__item}>
              <a href="#" className={styles.nav__link}></a>
            </li>
            <li className={styles.nav__item}>
              <Link to="/" className={styles.nav__link}>
                Inicio
              </Link>
            </li>
            <li className={styles.nav__item}>
              <Link to="/productos" className={styles.nav__link}>
                Productos
              </Link>
            </li>
            <li className={styles.nav__item}>
              <Link to="/contacto" className={styles.nav__link}>
                Contacto
              </Link>
            </li>
          </ul>
        </nav>
        <div className={styles.carritoItems}>{/* <h1>Tu carrito</h1> */}</div>

        <Switch>
          <Route exact path="/">
            <Header />
          </Route>
          <Route path="/productos">
            <Products />
          </Route>
          <Route path="/contacto">
            <Footer />
          </Route>
        </Switch>
      </div>
    </Router>
  );
}

export default Header;
