import styles from "../styles/Slider.module.scss";
import React from "react";
import useSlider from "./useSlider";

const slides = [
  {
    title: "Slide 1",
    color: "#56777A",
  },
  {
    title: "Slide 2",
    color: "#84ACAC",
  },
  {
    title: "Slide 3",
    color: "#FBA434",
  },
];

function Slider() {
  const slideWidth = 300;

  return (
    <div
      className="container"
      style={{
        backgroundColor: slides.color,
        width: slideWidth,
        height: slideWidth,
      }}
    >
      <div
        className="scroller"
        style={{
          // our counter offset will go here
          transform: `translate3d(-${offset * slideWidth}px,0,0)`,
          width: `${slides.length * slideWidth}px`,
        }}
      >
        {slides.map((slide, index) => (
          <div
            key={slide.title}
            className="slide"
            style={{
              backgroundColor: slide.color,
              width: slideWidth,
              height: slideWidth,
            }}
          >
            {slide.title}
          </div>
        ))}
      </div>
    </div>
  );
}

export default Slider;
